		$delivery_mst = array(
			'ID'        => $mst_id,
			'SYS_NUMBER_PREFIX' => $new_sys_number[1],
			'SYS_NUMBER_PREFIX_NUM' => $new_sys_number[2],
			'SYS_NUMBER' => $new_sys_number[0],
			'COMPANY_ID' => $mst_part_data->COMPANY_ID,
