					$this->db->query($gmts_prod_mst_sql);

					$mstIdArr[$orderId][$gmtsItemId][$countryId] = $id;

					/*if(!empty($production_mst)){
						$this->insertData($production_mst, 'PRO_GARMENTS_PRODUCTION_MST');
					}*/
				}
			}
