			$this->db->query($product_table_update);
		}

		$this->db->trans_complete();
		if ($this->db->trans_status() == TRUE) {
			return array("status" => "Success", "msg"=> "Issue ID = $issue_mrr");
		} else {
			return array("status" => "Failed");
		}

