				$all_data[$i]['LoadingDateTime']=$row['load'];
				$all_data[$i]['OffLoadingDateTime']=$row['unload'];
				$all_data[$i]['DyeingResult']=$dyeing_result[$row['result']];
				$i++;
			}
		}
		return $all_data;die;
