function diff_in_weeks_and_days($from, $to,$outputType="week") {
	$day   = 24 * 3600;
	$from  = strtotime($from);
	$to    = strtotime($to) + $day;
	$diff  = abs($to - $from);
	$weeks = floor($diff / $day / 7);
	$days  = $diff / $day - $weeks * 7;
	if($outputType=="week"){
		$out   = array();
		if ($weeks) $out[] = "$weeks Week" . ($weeks > 1 ? 's' : '');
		if ($days)  $out[] = "$days Day" . ($days > 1 ? 's' : '');
		return implode(', ', $out);
	}else{
		$totdays=$diff / $day ;
		return "$totdays Day" . ($totdays > 1 ? 's' : '');
	}
}
