function set_conversion_rate_quatation( $cid, $cdate )
{
	global $db_type;

	if($cdate=='')
	{
		if( $db_type==0) $cdate=date("Y-m-d",time());
		else $cdate=date("d-M-y",time());
	}
	if($cid==1)
	{
		return "1";
	}
	else
	{
		if($db_type==0) $cdate=change_date_format($cdate, "yyyy-mm-dd", "-",1);
		else $cdate=change_date_format($cdate, "d-M-y", "-",1);
		//echo $cdate;die;
		$queryText="select marketing_rate as conversion_rate from currency_conversion_rate where con_date<='".$cdate."' and currency=$cid and status_active=1 and is_deleted=0 order by con_date desc";
		//echo $queryText; die;
		$nameArray=sql_select( $queryText, '',$new_conn );
		if(count($nameArray)>0)
		{
			foreach ($nameArray as $result)
				if ($result[csf('conversion_rate')]!="") return  $result[csf("conversion_rate")]; else return "0";
		}
		else
			return "0";
	}
}

