			$item_wise_arr[$set_breck_downdata_arr[0]]['smv_pcs']=$set_breck_downdata_arr[2];
			$item_wise_arr[$set_breck_downdata_arr[0]]['smv_set']=$set_breck_downdata_arr[3];
