$update_prod=$update_trans=$update_trans_ord=$update_mrr=$update_transfer=1;
foreach($prod_rate_data as $prod_id=>$prod_rate)
{
	if($prod_id && $prod_rate)
	{
		$prod_query=execute_query("update product_details_master set avg_rate_per_unit=$prod_rate, stock_value=$prod_rate*current_stock where id=$prod_id");
	
		if($prod_query) $update_prod=1;
		else
		{
			$update_prod=0;
			echo "update product_details_master set avg_rate_per_unit=$prod_rate, stock_value=$prod_rate*current_stock where id=$prod_id";
			oci_rollback($con);
			die;
		}
		$trans_query=execute_query("update inv_transaction set cons_rate=$prod_rate, cons_amount=$prod_rate*cons_quantity, balance_amount=balance_qnty*$prod_rate where prod_id=$prod_id");
		if($trans_query) $update_trans=1;
		else
		{
			$update_trans=0;
			echo "update inv_transaction set cons_rate=$prod_rate, cons_amount=$prod_rate*cons_quantity, balance_amount=balance_qnty*$prod_rate where prod_id=$prod_id";
			oci_rollback($con);
			die;
		}
		
		$queryOrderRate=execute_query("update inv_transaction b set (b.order_rate,b.order_amount)=(select $prod_rate/nvl(a.exchange_rate,1),($prod_rate/nvl(a.exchange_rate,1))*b.order_qnty from inv_receive_master a where a.id=b.mst_id and a.entry_form=1) where b.transaction_type=1 and b.prod_id=$prod_id "); 
		if($queryOrderRate) $update_trans_ord=1;
		else
		{
			$update_trans_ord=0;
			echo "update inv_transaction b set (b.order_rate,b.order_amount)=(select $prod_rate/nvl(a.exchange_rate,1),($prod_rate/nvl(a.exchange_rate,1))*b.order_qnty from inv_receive_master a where a.id=b.mst_id and a.entry_form=1) where b.transaction_type=1 and b.prod_id=$prod_id";
			oci_rollback($con);
			die;
		}
		
		$queryMrr=execute_query("update inv_mrr_wise_issue_details set rate=$prod_rate, amount=$prod_rate*issue_qnty where prod_id=$prod_id");
		if($queryMrr) $update_mrr=1;
		else
		{
			$update_mrr=0;
			echo "update inv_mrr_wise_issue_details set rate=$prod_rate, amount=$prod_rate*issue_qnty where prod_id=$prod_id";
			oci_rollback($con);
			die;
		}

		$queryTransfer=execute_query("update inv_item_transfer_dtls set rate=$prod_rate, transfer_value=$prod_rate*transfer_qnty where from_prod_id=$prod_id");
		if($queryTransfer) $update_transfer=1;
		else
		{
			$update_transfer=0;
			echo "update inv_item_transfer_dtls set rate=$prod_rate, transfer_value=$prod_rate*transfer_qnty where from_prod_id=$prod_id";
			oci_rollback($con);
			die;
		}
		
	}
